const text = $input.first().json.text || '';

// Clean text function
function cleanText(t) {
  if (!t) return '';
  const str = String(t);
  return str.replace(/[\u00A0\u2000-\u200B\u2028\u2029]/g, ' ').replace(/\s+/g, ' ').trim();
}

// Extract edition number
function extractEdition(t) {
  if (!t) return null;
  const str = String(t);
  const patterns = [
    /([0-9]+)ª\s*[Ee]di[çc][ãa]o/i,
    /([0-9]+)a\s*[Ee]dicao/i,
    /[Ee]di[çc][ãa]o\s*([0-9]+)/i
  ];
  for (const p of patterns) {
    const m = str.match(p);
    if (m && m[1]) return m[1] + 'ª Edição';
  }
  return null;
}

// Extract publication date
function extractDate(t) {
  if (!t) return null;
  const str = String(t);
  const months = ['Janeiro', 'Fevereiro', 'Março', 'Abril', 'Maio', 'Junho', 'Julho', 'Agosto', 'Setembro', 'Outubro', 'Novembro', 'Dezembro'];
  const pattern = new RegExp(`(${months.join('|')})\s+de\s+(20[0-9]{2})`, 'i');
  const m = str.match(pattern);
  return m ? `${m[1]} de ${m[2]}` : null;
}

// Extract main headline
function extractHeadline(t) {
  if (!t) return null;
  const str = String(t);
  const lines = str.split('\n').map(l => cleanText(l)).filter(l => l.length > 0);
  for (let i = 2; i < Math.min(10, lines.length); i++) {
    const line = lines[i];
    if (line.length > 10 && line.length < 80 && 
        !line.match(/FIQUE|PUBLICIDADE|Quem Somos|Ficha Técnica/i) &&
        line !== line.toLowerCase()) {
      return line;
    }
  }
  return null;
}

// Extract editorial team
function extractEditorialTeam(t) {
  if (!t) return [];
  const str = String(t);
  const team = [];
  const sectionMatch = str.match(/Ficha\s+Técnica([\s\S]{0,800})/i);
  if (sectionMatch) {
    const section = sectionMatch[1];
    const lines = section.split('\n').map(l => cleanText(l)).filter(l => l.length > 0);

    for (let i = 0; i < lines.length - 1; i++) {
      const name = lines[i];
      const role = lines[i + 1];

      if (name && role && 
          name.length > 5 && name.length < 60 &&
          role.length > 10 && role.length < 80 &&
          name.match(/^[A-ZÁÉÍÓÚÃ][a-záéíóúãçõ]+(\s+[A-ZÁÉÍÓÚÃ][a-záéíóúãçõ]+)+$/) &&
          role.match(/^[A-Z]/)) {
        team.push(`${name} - ${role}`);
        i++;
      }
    }
  }
  return team.slice(0, 8);
}

// Extract article titles
function extractArticleTitles(t) {
  if (!t) return [];
  const str = String(t);
  const titles = [];
  const indexMatch = str.match(/Índice([\s\S]{0,3000})/i);
  if (indexMatch) {
    const indexSection = indexMatch[1];
    const lines = indexSection.split('\n').map(l => cleanText(l)).filter(l => l.length > 0);

    for (const line of lines) {
      const cleanTitle = line.replace(/^[\d\sں•]+/, '').replace(/\s+\d+$/, '').trim();
      if (cleanTitle.length > 15 && cleanTitle.length < 200 && 
          !cleanTitle.match(/PUBLICIDADE|Índice/i) &&
          cleanTitle !== cleanTitle.toUpperCase() &&
          /[a-záéíóúãç]/.test(cleanTitle)) {
        titles.push(cleanTitle);
      }
    }
  }
  return [...new Set(titles)].slice(0, 30);
}

// Extract featured people
function extractPeople(t) {
  if (!t) return [];
  const str = String(t);
  const people = new Set();

  const featuredPattern = /ں\s+ں\s+ں\s+ں\s+([A-ZÁÉÍÓÚÃ][a-záéíóúãçõ]+\s+[A-ZÁÉÍÓÚÃ][a-záéíóúãçõ]+(?:\s+[A-ZÁÉÍÓÚÃ][a-záéíóúãçõ]+)?)/g;
  let match;
  while ((match = featuredPattern.exec(str)) !== null) {
    const name = cleanText(match[1]);
    if (name.length > 5 && name.length < 50) {
      people.add(name);
    }
  }

  const nameRolePattern = /([A-ZÁÉÍÓÚÃ][a-záéíóúãçõ]+\s+[A-ZÁÉÍÓÚÃ][a-záéíóúãçõ]+(?:\s+[A-ZÁÉÍÓÚÃ][a-záéíóúãçõ]+)?)\s+(?:é|foi|está|trabalha|fundou|CEO|Diretor|Gestor)/gi;
  while ((match = nameRolePattern.exec(str)) !== null) {
    const name = cleanText(match[1]);
    if (name.length > 5 && name.length < 50) {
      people.add(name);
    }
  }

  return Array.from(people).slice(0, 25);
}

// Extract companies
function extractCompanies(t) {
  if (!t) return [];
  const str = String(t);
  const companies = new Set();
  const patterns = [
    'Kula', 'Kunona', 'Tablu', 'Chairman Studios', 'Wei', 'TechLit', 'Jetstream', 'Synchron',
    'Tsolnet', 'Moza Banco', 'Vodacom', 'OpenAI', 'Microsoft', 'Apple', 'Google', 'Amazon',
    'Raxio', 'Newchip', 'IBM', 'Standard Bank', 'Fintech.MZ', 'GoMarket', 'Baoba Hub',
    'Kudziva', 'Ruca', 'Dino Valeta', 'CES', 'Catalyst Fund',
    'Spot Comunicação', 'Lifesten Health', 'TechLit África', 'Jetstream África'
  ];

  for (const p of patterns) {
    if (str.includes(p)) companies.add(p);
  }

  return Array.from(companies);
}

// Extract technical terms
function extractTechTerms(t) {
  if (!t) return [];
  const str = String(t);
  const terms = new Set();
  const pattern = /\b(IA|UX|UI|CEO|CTO|CFO|KYC|TIC|BCI|API|SDK|CES|IoT|SaaS|PaaS|AI|ML|UEM|ISCTEM|EUA|NASA)\b/gi;
  let match;
  while ((match = pattern.exec(str)) !== null) {
    terms.add(match[1]);
  }
  return Array.from(terms);
}

// Extract technologies
function extractTechnologies(t) {
  if (!t) return [];
  const str = String(t);
  const techs = new Set();
  const techList = ['ChatGPT', 'M-Pesa', 'Inteligência Artificial', 'Big Data', 'Cloud', 'Blockchain', 'Machine Learning', 'energia solar', 'energia renovável', 'fintech', 'agritech', 'edtech', 'robô de colheita', 'carro voador'];

  for (const tech of techList) {
    if (str.toLowerCase().includes(tech.toLowerCase())) {
      techs.add(tech);
    }
  }

  return Array.from(techs);
}

// Extract products
function extractProducts(t) {
  if (!t) return [];
  const str = String(t);
  const products = new Set();
  const productList = ['Aska A5', 'Nanoleaf 4D', 'robô de colheita', 'MacBook', 'iPhone', 'Google Gemini', 'Zoundream', 'carro voador elétrico'];

  for (const p of productList) {
    if (str.toLowerCase().includes(p.toLowerCase())) {
      products.add(p);
    }
  }

  return Array.from(products);
}

// Extract funding
function extractFunding(t) {
  if (!t) return [];
  const str = String(t);
  const funding = new Set();
  const patterns = [
    /([0-9]+(?:[.,][0-9]+)?)\s*(?:mil(?:h[õo]es)?|bilh[õo]es?)\s+de\s+d[óo]lares?/gi,
    /([0-9]+)\s*mil(?:h[õo]es)?\s*(?:de\s+)?d[óo]lares?/gi,
    /([0-9]+)\s*milh[õo]es\s*(?:de\s+)?d[óo]lares?/gi
  ];

  for (const p of patterns) {
    let m;
    while ((m = p.exec(str)) !== null && funding.size < 15) {
      funding.add(m[0]);
    }
  }

  return Array.from(funding);
}

// Extract events
function extractEvents(t) {
  if (!t) return [];
  const str = String(t);
  const events = new Set();
  const eventList = ['CES 2023', 'Newchip', 'Hanga Pitchfest', 'Africa Tech Summit', 'Demo Week', 'Catalyst Fund', 'Newchip Demo Week', 'Plug and Play Friday Pitch Session'];

  for (const e of eventList) {
    if (str.includes(e)) events.add(e);
  }

  return Array.from(events);
}

// Extract universities
function extractUniversities(t) {
  if (!t) return [];
  const str = String(t);
  const unis = new Set();
  const uniList = ['ISCTEM', 'UEM', 'Universidade Eduardo Mondlane', 'Universidade Pedagógica', 'HEC Paris', 'Universidade Hasselt', 'University of Global Health Equity', 'Olivet University'];

  for (const u of uniList) {
    if (str.includes(u)) unis.add(u);
  }

  return Array.from(unis);
}

// Extract statistics
function extractStatistics(t) {
  if (!t) return [];
  const str = String(t);
  const stats = new Set();
  const patterns = [
    /([0-9]+(?:[.,][0-9]+)?)\s*(?:mil|milh[ãa]o|bilh[ãa]o|utilizadores|membros|visualiza[çc][õo]es|pessoas|usuários)/gi,
    /([0-9]+)\s*(?:mil|milh[ãa]o)\s*(?:de\s+)?d[óo]lares?/gi
  ];

  for (const p of patterns) {
    let m;
    while ((m = p.exec(str)) !== null && stats.size < 20) {
      stats.add(m[0]);
    }
  }

  return Array.from(stats);
}

// Extract locations
function extractLocations(t) {
  if (!t) return [];
  const str = String(t);
  const locs = new Set();
  const locationList = ['Moçambique', 'Maputo', 'Quênia', 'Ruanda', 'Nigéria', 'EUA', 'Angola', 'África do Sul', 'Califórnia', 'Uganda', 'Egito', 'Las Vegas', 'Senegal', 'Marrocos', 'Namíbia', 'Zâmbia', 'Zimbabwe', 'Malawi', 'Botswana'];

  for (const l of locationList) {
    if (str.includes(l)) {
      locs.add(l);
    }
  }

  return Array.from(locs).slice(0, 20);
}

// Extract sectors
function extractSectors(t) {
  if (!t) return [];
  const str = String(t);
  const sectors = new Set();
  const sectorList = ['fintech', 'agritech', 'edtech', 'energia renovável', 'logística', 'saúde', 'tecnologia médica', 'telecomunicações', 'banco', 'seguros'];

  for (const s of sectorList) {
    if (str.toLowerCase().includes(s.toLowerCase())) {
      sectors.add(s);
    }
  }

  return Array.from(sectors);
}

// Extract articles with content
function extractArticlesWithContent(t) {
  if (!t) return [];
  const str = String(t);
  const articles = [];
  const sections = str.split(/\n\n+/);
  let currentTitle = null;
  let currentContent = [];

  for (const section of sections) {
    const clean = cleanText(section);

    if (clean.match(/^ں+/) || (clean.length > 20 && clean.length < 150 && 
        clean === clean.toUpperCase() && !clean.includes('PUBLICIDADE'))) {

      if (currentTitle && currentContent.length > 0) {
        articles.push({
          title: currentTitle.replace(/^ں+\s*/, ''),
          content: currentContent.join('\n\n').substring(0, 8000)
        });
      }

      currentTitle = clean;
      currentContent = [];
    } else if (currentTitle && clean.length > 50 && !clean.includes('PUBLICIDADE')) {
      currentContent.push(clean);
    }
  }

  if (currentTitle && currentContent.length > 0) {
    articles.push({
      title: currentTitle.replace(/^ں+\s*/, ''),
      content: currentContent.join('\n\n').substring(0, 8000)
    });
  }

  return articles.slice(0, 20);
}

// Extract awards
function extractAwards(t) {
  if (!t) return [];
  const str = String(t);
  const awards = new Set();
  const awardPatterns = ['CNN Hero', 'CES.*?Award', 'Inovador.*?do ano', 'heroína do ano', 'prêmio de melhor subsidiária'];

  for (const p of awardPatterns) {
    const regex = new RegExp(p, 'gi');
    const matches = str.match(regex);
    if (matches) {
      matches.forEach(m => awards.add(m));
    }
  }

  return Array.from(awards);
}

// Extract total pages
function extractTotalPages(t) {
  if (!t) return null;
  const str = String(t);
  const pageNumbers = str.match(/\b([0-9]{1,3})\b/g);
  if (pageNumbers) {
    const numbers = pageNumbers.map(n => parseInt(n)).filter(n => n < 200);
    return numbers.length > 0 ? Math.max(...numbers) : null;
  }
  return null;
}

// Extract advertising brands
function extractAdvertisingBrands(t) {
  if (!t) return [];
  const str = String(t);
  const brands = ['Baoba Hub', 'GoMarket', 'Kudziva', 'Ruca', 'Dino Valeta'];
  return brands.filter(b => str.includes(b));
}

// Main extraction
const textStr = String(text);
return {
  json: {
    edition_number: extractEdition(textStr),
    publication_date: extractDate(textStr),
    main_headline: extractHeadline(textStr),
    editorial_team: extractEditorialTeam(textStr),
    featured_people: extractPeople(textStr),
    featured_companies: extractCompanies(textStr),
    article_titles: extractArticleTitles(textStr),
    technical_terms: extractTechTerms(textStr),
    technologies_mentioned: extractTechnologies(textStr),
    products_services: extractProducts(textStr),
    title_with_associated_content: extractArticlesWithContent(textStr),
    international_news: [],
    african_news: [],
    mozambique_focus: [],
    funding_investments: extractFunding(textStr),
    events_mentioned: extractEvents(textStr),
    universities_institutions: extractUniversities(textStr),
    key_statistics: extractStatistics(textStr),
    geographic_locations: extractLocations(textStr),
    industry_sectors: extractSectors(textStr),
    total_pages: extractTotalPages(textStr),
    advertising_brands: extractAdvertisingBrands(textStr),
    awards_recognition: extractAwards(textStr),
    extracted_at: new Date().toISOString(),
    word_count: textStr.split(/\s+/).filter(w => w.length > 0).length,
    text_length: textStr.length
  }
};

// Loop over input items and add 'title' and 'content' fields to the JSON of each one
for (const item of $input.all()) {
  let title = '';
  let content = '';

  // Check all fields to find the one with HTML
  let sourceText = item.json.output || 
                   item.json.cleanedText || 
                   item.json.html || 
                   item.json.content ||
                   item.json.body || 
                   item.json.text || '';

  // Extract title from <h1 class="entry-title">TEXT</h1>
  // This regex handles escaped quotes, regular quotes, and any attributes
  const titleRegex = /<h1\s+class=["\\]?entry-title["\\]?[^>]*>(.*?)<\/h1>/s;
  const titleMatch = sourceText.match(titleRegex);

  if (titleMatch && titleMatch[1]) {
    // Get the title text, remove any HTML tags, and unescape
    title = titleMatch[1]
      .replace(/<[^>]*>/g, '') // Remove any HTML tags inside
      .replace(/\\"/g, '"')     // Unescape quotes
      .replace(/&quot;/g, '"')  // Decode HTML entities
      .trim();

    // Remove the entire <h1> block from content
    content = sourceText.replace(titleMatch[0], '').trim();
  } else {
    // If no <h1> found, use the whole text as content
    content = sourceText;
  }

  // Clean up excessive whitespace
  content = content.replace(/\n\s*\n+/g, '\n').trim();

  // Add the new fields
  item.json.title = title;
  item.json.content = content;
}

return $input.all();
